void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.0 *(fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Time varying pixel color
    vec3 col_back = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 col_b  = vec3(0.0);
    vec3 col_w  = vec3(1.0);

    vec3 col  = col_w;

    // Output to screen
    vec2 centr = vec2(0.0,0.0); 

    float r = (cos(iTime * 1.0) + 1.0) * 0.24 +0.5;
    float d = distance(uv,centr);

    float x0 = step(r,d); 
    float x1 = step(r-0.1,d);  

    float c1  = distance(uv,centr);

    vec2 uvToCenter = uv - centr;

    float angle = (degrees(atan( uvToCenter.x , uvToCenter.y ) + 3.1415) ) / (360.0);

    float angle315 = 315.0/360.0;
    float angle45  = 45.0/360.0;
    float angle180 = 180.0/360.0;
    float angle270 = 270.0/360.0;

    float col_ring = 1.0 - step(x1,x0);

    float rs = step(uv.x,0.05);
    float ls = step(-0.05,uv.x);
    float us = step(uv.y,r); 
    float ds = step(-r ,uv.y);

    float line = (rs * ls * us * ds) ;

    col = vec3(line + col_ring); 
    float col_d = step(mod((angle315 + angle),1.0),angle270);

    angle = (degrees(atan( uvToCenter.x , uvToCenter.y - 0.12) + 3.1415) ) / (360.0);

    float col_h = step(angle270,mod((angle315 + angle),1.0) ); 

    col +=  vec3(col_d * col_h);
    float temp = step(x0,r);
    col *= clamp(temp,0.0,1.0);

    //col *= vec3(sin(iTime *0.5),0.5,sin(iTime * 1.11));
    col += col_back;

    fragColor = vec4(col,1.0);
}
