void mainImage(out vec4 color, vec2 coord) {
    vec2 mouse = mix(
        vec2(iTime * 100.0),
        iMouse.xy,
        step(0.0, iMouse.z)) * 0.01;

    vec2 glass_offset = sin(coord * 0.1 - mouse) * 10.0;
    vec2 glass_coord = coord + glass_offset;

    vec2 ps = vec2(1.0) / iResolution.xy;
    vec2 uv = coord * ps;

    vec2 glass_uv = glass_coord * ps;

    color = texture(iChannel0, glass_uv);
}
