float dist(vec2 p0, vec2 pf){return sqrt((pf.x-p0.x)*(pf.x-p0.x)+(pf.y-p0.y)*(pf.y-p0.y));}
float graph(float x){return (cos(x*20.0+iTime*2.0))/20.0 + 0.12;}
float graph2(float x){return (cos(x*20.0-iTime*2.0))/20.0 + 0.1;}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 bc = vec2(iResolution.x*0.5,uv.y);

    //wave 1
    if ( uv.y < graph(uv.x) ) fragColor = vec4(0.1,0.3,0.8,1.0);
        else fragColor = vec4(0.1, 0.9, 1.0, 1.0);

    // rainbow
    float d = distance(bc.xy,fragCoord.xy)*0.01;
        if (d < 2.0) ;
        else if (d < 2.1) fragColor = vec4(0.56, 0.0, 1.0, 1.0);
        else if (d < 2.2) fragColor = vec4(0.294, 0.0, 0.51, 1.0);
        else if (d < 2.3) fragColor = vec4(0.0, 0.0, 1.0, 1.0);
        else if (d < 2.4) fragColor = vec4(0.0, 1.0, 0.0, 1.0);
        else if (d < 2.5) fragColor = vec4(1.0, 1.0, 0.0, 1.0);
        else if (d < 2.6) fragColor = vec4(1.0, 0.5, 0.0, 1.0);
        else if (d < 2.7) fragColor = vec4(1.0, 0.0, 0.0, 1.0);

    //wave 2
    if ( uv.y < graph2(uv.x) ) fragColor = vec4(0.0,0.0,1.0,1.0);

    // sun
    vec2 sunCoords = vec2(iResolution.x*0.1,iResolution.y*0.8);
    float sunDistOld = distance(sunCoords.xy,fragCoord.xy)*0.01;
    float sunDist = distance(sunCoords.xy,fragCoord.xy)*0.01*(sin(iTime*1.5)/3.0 +2.0);
    if (sunDist < 1.3) fragColor = vec4(0.1, 0.9, 1.0, 1.0);
    if (sunDist < 1.1) fragColor.rgb = pow(fragColor.rgb, vec3(.454545)); 
    if (sunDistOld < 0.2) fragColor = vec4(1.0, 1.0, 0.0, 1.0); 
}
