void mainImage( out vec4 Q, vec2 U )
{
    Q = A(U);
    Neighborhood;
    vec3 no = normalize(vec3(n.w-s.w+n.z-s.z,e.w-w.w+e.z-w.z,.25)),
         re = reflect(no,vec3(0,0,1)),
         ra = refract(no,vec3(0,0,1),.5);
    Q = sin(2.*Q.w+Q.z*vec4(1,2,3,4));
    Q *= 0.5+0.5*texture(iChannel2,re);
    Q *= 0.5+0.5*texture(iChannel2,ra);
}

Main {
	Q = A(U);
    Neighborhood;
    Q.z += 0.25*(w.x-e.x+s.y-n.y);
    Q.zw += vec2(-.25,1)*(Q.w)*0.25*(w.w*w.x-e.w*e.x+s.w*s.y-n.w*n.y);
    n = D(U+vec2(0,1)), e = D(U+vec2(1,0)), s = D(U-vec2(0,1)), w = D(U-vec2(1,0));
	Q.z += 0.25*(w.x-e.x+s.y-n.y);
}

Main {
	Q = A(U);
    Neighborhood;
    // Gradient of Pressure
    vec4 d = D(U);
    Q.xy += d.xy/(1.+3.*Q.w);
	Q.y -= 5e-4*Q.w;
    // Boundary conditions :
    if (iMouse.z>0.) {
    	if (length(iMouse.xy-U)<10.)
            Q.xyw = vec3(0.2,0,1);
    }else if (length(U-vec2(0.2,0.8)*R)<4.) Q = vec4(0.2,-0.3,Q.z,1);
    if (iFrame < 1) {
    	if (length(0.5*R-U)<10.)
            Q.xyw = vec3(0,0.5,1);
    }
    if (U.x<2.||U.y<2.||R.x-U.x<2.||R.y-U.y<2.)
        Q.xy *= 0.;
}

Main {
	Neighborhood;
    Q.xy=0.25*vec2(w.z-e.z,s.z-n.z);
    Q.zw=0.25*vec2(w.w-e.w,s.w-n.w);
    if (U.x<2.||U.y<2.||R.x-U.x<2.||R.y-U.y<2.)
        Q *= 0.;

}
