// reverse pattern of https://shadertoy.com/view/WltBzM

#define D   length( mod( U += T/2. , T ) - R )               //
#define P   clamp( ( min( D, D ) -.6*R.y )/6. ,0.,1.)    // draw an hexa tiling of disks

void mainImage(out vec4 O, vec2 U) {
    U *= 4.;
    vec2 R = iResolution.yy/12., T = vec2( 2, 3.5 )*R;
    O += P -O;

    U = 1.1*U + 10.*iTime;
    O *= P;
}
