#define MAXITER 100
#define MAXVAL 5.
#define PI 3.14159265359

vec3 getColorf(float iter,vec2 z){

    vec3 color;
    color.r=float(mod(iter,10.))/9.;
    color.b=float(mod(iter/10.,10.))/9.;
    color.g=.5+.5*sin(iter/10.-log2(1.+log2(1.+dot(z,z))));
    return color;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    vec2 uv = fragCoord.xy/iResolution.xy;
    uv-=.5;
//   float zoom=.004+iMouse.x/iResolution.x;
  float zoom=.75+.4*(cos(PI+.07*iTime));

    uv*=2.*pow(zoom,8.);
    vec3 color;
    vec2 c=uv+vec2(-.74515,.1858);
   // vec2 c=uv+vec2(-.68435,.322500);
    vec2 z=vec2(0.);
    float iter=0.;
    float warp=2.;//-(iMouse.y/iResolution.y);
    for(int i=0;i<MAXITER;++i){
        z=vec2(z.x*z.x-z.y*z.y,warp*z.x*z.y)+c;
        if (length(z)>MAXVAL) break;
        iter++;
    }
    color=getColorf(iter,z);    
    fragColor = vec4(color,1.0);
}
