renderpass.code:
precision highp float;
precision highp int;

void mainImage( out vec4 fd, in vec2 fc )
{
    const float ishift = 100.;
    const float shift = 1./ishift;
    ivec2 c = ivec2(fc);

    float a = float(c.x)*shift;
    float b = float(c.y)*shift;

    float p1 = (1.+a)*(1.+b)-(1.+a+b);
    float p2 = a*b;

    int p1i = int(round(p1*(ishift*ishift)));
    int p2i = int(round(p2*(ishift*ishift)));

    fd = vec4(0.);

    fd.r = float(abs(p1-p2)<1e-10);

    //uncomment one of these:
    //fd.g = -float(p1i==p2i);
    //fd.g = -float(p1i);
    //fd.g = -float(p2i);

    fd.b=0.;
}
