// https://creativecommons.org/licenses/by-nc/4.0/
// -----------------------------------------------------


void mainImage(out vec4 O,in vec2 U)
{
float t=iTime,ft=fract(t),d,f=0.,c=0.,s=0.,b=0.;
vec2 R=iResolution.xy;
vec2 u=1.45*((U-.5*R)/min(R.x,R.y)),p=mod(-.25*t,2.5)*u;
d=fract(.1*t)+.8-smoothstep(0.,.7,length(p));
int sw=int(.5*t)%3;
for(float i=0.;i<16.;i++)
{
vec2 CS=p*sin(.5*t+.4*i+vec2(1.57,0))*vec2(6,10);
b=sw==0?-1.:sw==1?-CS.x:CS.x+cos(p.x*2.5);
f+=.0125/abs(floor(-d+fract(f-2.5*d+(mod(t,8.)>2.?.1:ft-.5)))+CS.y-CS.x*fract(2.*d)*b);
}
U=abs(fract(.25*U)-.5);
(O=ft>.5&&t>17.&&t<21.5?
vec4(sin(12.*t*fract(.05*R.x*u.x))/cos(1.-u.y))
:.05+2.*f-sqrt(.5*ft*acos(2.5*u.y)*f+vec4(1,.1,.2,1))+.06*(U.x-U.y)+vec4(.5+.5*cos(t),.15,.25,1));
}

// -----------------------------------------------------
// riff #2022 by nabr
// https://www.shadertoy.com/view/WlyGDy
// License Creative Commons Attribution-NonCommercial 4.0 International (CC BY-NC 4.0)
// https://creativecommons.org/licenses/by-nc/4.0/
// -----------------------------------------------------


vec2 mainSound( in int samp, float t )
{
    float p,s,s1,s2;
    p=float[](470.,500.,320.)[int(12.*t)%3],
    s=sin(p*.025/fract(16.*t));s1=s;
    if(fract(t)>.5&&t>17.&&t<21.)t=1.25*mod(1.-t,2.5*t);
    if(int(t)%8>4)s1=.25*sin(p*12.2*(int(t)%16<8?t:.5*t));
    s2=2.+s1/(3./fract(t)+s1)*(s1+s-.5);
    s2=2.*s2+cos(s2)*s2;
    s2*=12.*cos(s2)*s1*exp(-5.1*fract(3.*t));
    s2*=exp(-20.*fract(6.*t));
    return vec2(clamp(s2*-.5,-.5,.5));
}
