void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 pos1 = vec2(0.0+sin(iTime)/4.0,0.0);
    vec2 pos2 = vec2(0.2*cos(iTime),-0.2*sin(iTime));

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5; // set center/origo to vec2(0.0,0.0);

    uv.x *= iResolution.x/iResolution.y; // Aspect ratio

    if(mod(distance(uv, pos1),0.05) > 0.025)
    {
        vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
        fragColor = vec4(col, 1.0);
    }
    else
        fragColor = vec4(0.0);

    // Draw the second thing of top of first.
    if(mod(distance(uv, pos2),0.05) > 0.025)
    {
        vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
        fragColor = vec4(col, 1.0);
    }

}
