#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)

#define Main void mainImage( out vec4 Q, in vec2 U )

#define Them vec4 n = A(U+vec2(0,1)), e = A(U+vec2(1,0)), s = A(U-vec2(0,1)), w = A(U-vec2(1,0)), m = 0.25*(n+e+s+w);

#define ro(a) mat2(cos(a),-sin(a),sin(a),cos(a))

float an (vec2 a, vec2 b) {
	return atan(a.x*b.y-a.y*b.x,dot(a.xy,b.xy));
}

Main
{
    U -= 0.49*R;
    U *= (1.-.005*length(U)/R.x)*ro(.001);
    U += 0.49*R;
	Q = A(U);
    Them;
    Q += .3*(m-Q);
    Q.xy *= ro(.02*(n.w-s.w+e.z-w.z));
    if (length(Q.xy)>0.)Q.xy = normalize(Q.xy);
    Q.zw += vec2(an(e.xy,w.xy),an(n.xy,s.xy));
    if (length(Q.zw)>0.)
    	Q.zw = mix(Q.zw,normalize(Q.zw),.8);
    if (iMouse.z>0.&&length(U-iMouse.xy)<30.)
        Q.xy *= ro(.1);

    if (iFrame <1) Q = vec4(sin(.1*U.y),cos(.1*U.x),0,0);
}

