#define STEPS 128
#define E 0.001
#define gamma vec3(2.2)

const vec3 AMBIENT = vec3(0.2, 0.4, 0.64);
const vec3 LC1 = vec3(0.3, 0.6, 0.8);
const vec3 LC2 = vec3(0.6, 0.4, 0.3);

// 3D noise function (IQ)
float noise(vec3 p){
	vec3 ip = floor(p);
    p -= ip;
    vec3 s = vec3(7.0,157.0,113.0);
    vec4 h = vec4(0.0, s.yz, s.y+s.z)+dot(ip, s);
    p = p*p*(3.0-2.0*p);
    h = mix(fract(sin(h)*43758.5), fract(sin(h+s.x)*43758.5), p.x);
    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z);
}

float scene(in vec3 p){

    float pl = dot(p, normalize(vec3(0.0, 1.0, 0.0)))+1.0;
    pl -= smoothstep(0.0, 1.0, noise(p*(sqrt(5.0)*0.5 + 0.5)*0.2+iTime*0.2));

    vec3 pp = p;
    vec2 n = vec2(2.7, 8.0);
    vec2 dif = n*0.5;
    pp.xz = mod(p.xz+dif, n)-dif;
    vec2 id = abs(floor((p.xz+dif)/n));
    id.x = 1.0+sin(id.x);

    vec3 d = abs(pp)-vec3(1.0,
             abs(cos(id.x)+sin(id.y))+0.5,
             1.0);
    float sp = length(max(max(d.x, d.y), d.z));

    vec3 gd = abs(pp)-vec3(n.x, 5.0, n.y)*0.5;

    float guard = -length(max(max(gd.x, gd.y), gd.z));
    guard = abs(guard) + n.x*0.1;

    return min(min(sp, guard), pl);
}

float march(in vec3 o, in vec3 d, in float far, out vec3 p, out bool hit){
    float t = 0.0;
    for(int i = 0; i < STEPS; ++i){
        p = o + d*t;
        float dist = scene(p);
        t += dist;
        if(dist <= E || t >= far){
            if(dist <= E ){
            	hit = true;
            }
            break;
        }
    }

    return t;
}

vec3 normal(in vec3 p){
    vec3 eps = vec3(E, 0.0, 0.0);
    return normalize(vec3(
        scene(p+eps.xyy) - scene(p-eps.xyy),
        scene(p+eps.yxy) - scene(p-eps.yxy),
        scene(p+eps.yyx) - scene(p-eps.yyx)
    ));
}

vec3 shade(in vec3 p, in vec3 d, in vec3 ld, in vec3 lp){

    vec3 n = normal(p);
    float lamb = max(dot(n,ld), 0.0);
    vec3 angle = reflect(n, ld);
    float spec = pow(max(dot(d, angle), 0.0), 20.0);

    float l = distance(p, lp);
    bool hit = false;
    vec3 sp = vec3(0.0);
    float st = march(p+E*n*2.0, ld, l, p, hit);
    float s = 1.0;
    if(hit){
        s = 0.0;
    }

    return (lamb*vec3(0.5, 0.0, 0.5)*0.5 + spec*vec3(1.0, 0.5, 1.0)*0.8)*s;
}

vec3 fog(in vec3 col, in vec3 p, in vec3 ro, in vec3 rd, in vec3 ld, in vec3 lc){
    float d = length(p-ro);
    float sa = max(dot(rd, -ld), 0.0);
    float fa = 1.0-exp(-d*0.05);
    vec3 fc = mix(vec3(0.64, 0.62, 0.6), lc, pow(sa, 4.0));
    return mix(col, fc, fa);
}

mat3 camera(in vec3 o, in vec3 t, in vec3 up){

    vec3 z = normalize(t-o);
    vec3 x = normalize(cross(z, up));
    vec3 y = normalize(cross(x, z));

    return mat3(x, y, z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv*2.0-1.0;//scaling from -1 to 1
    uv.x *= (iResolution.x/iResolution.y);

    vec3 ro = vec3(0.0, 2.5, iTime);
    vec3 rt = vec3(0.0, -4.0, 10.0+iTime);
    vec3 up = vec3(0.0, 1.0, 0.0);

    mat3 cam = camera(ro, rt, up);
    vec3 rd = normalize(cam*vec3(uv, radians(60.0)));

    vec3 p = vec3(0.0);
    bool hit = false;
    float t = march(ro, rd, 40.0, p, hit);
    vec3 col = AMBIENT * 0.6;

    vec3 lp = vec3(2.0*sin(iTime*0.5)+iTime, -4.0, 1.0+2.0*cos(iTime*0.5)+iTime);
    vec3 lt = vec3(0.0, 0.0, -10.0);
    vec3 ld = normalize(lt-lp);
    vec3 ld2 = normalize(ro-rt);

    if(hit){
        vec3 c = shade(p, rd, ld, lp);
        c += shade(p, rd, -ld2, ro);
        c *= 0.5;
        col += c;
    }

    col = fog(col, p, ro, rd, ld, LC1);
    col += fog(col, p, ro, rd, -ld2, LC2);
    col *= 0.5;

    // Output to screen
    fragColor = vec4(pow(col, 1.0/gamma),1.0);
}
