void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy - .5 *iResolution.xy)/iResolution.y;
    float ratio = iResolution.x/iResolution.y;
	vec2 polar =  vec2(atan(uv.x,uv.y), length(uv));

    uv = vec2(polar.x/6.2831+.5 + iTime * sin(.5) - polar.y, polar.y);

    float sizeTime = abs(sin(iTime * .9) * 2.0);
   	float x = uv.x * 10.;
    float m = min(fract(x),fract(10.-x)); 
    float c = smoothstep(0.2,.1,m* sizeTime+.5-uv.y);

    vec2 center2 = vec2(0.5*ratio  /*sin(iTime)*/, 0.5);
    float dist = distance(uv, center2);

    // Time varying pixel color
	vec3 color = c * vec3(1);
    color += vec3(0.,0,abs(cos(iTime)) +1.);
    color *= dist;
    // Output to screen
    fragColor = vec4(color,1.0);
}
