void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;


    vec2 st = uv;
    st.x -= sin(iTime)*.4;
    st.y -= cos(iTime*.3)*.23;
    float circleMask = 1.-smoothstep(.2,.284, length(st));
    uv.x *=1.6;

    uv.x += sin(uv.x+iTime)*.1+2.;
    uv.x += cos(uv.y+iTime*1.4)*.08+.3;

    float curtain = fract(abs(uv.x*5.));

    curtain= curtain*curtain*(1.-curtain)*4.-uv.y*.7;
    vec3 color = mix(vec3(uv.x*.4,.3,.2), vec3(1.,.5,.3), -uv.y);
    vec3 circleCol = circleMask*mix(vec3(1.,.8,0.), vec3(1.,1.,1.), circleMask);

    vec3 blueTint =vec3(0.8,0.,1.)*(.6-curtain)*.29;
    blueTint *= 0.9-circleMask;

    vec3 yellowTint = mix(vec3(1.,.8,0.), vec3(1.,1.,0.), curtain);
    yellowTint *= circleMask*.35;

    vec3 col = vec3(color*curtain+blueTint+yellowTint);
    col += circleCol*.4;

    fragColor = vec4(col,1.0);
}
