// variant of https://shadertoy.com/view/wtKSzz

void mainImage(out vec4 O, vec2 u) {
    for ( vec2 A = iResolution.xy,
               U = ( u+u - A ) / A.y;
          O.a++ < 26.;
          O = max(O, ( 2.- abs(A)/fwidth(A) ).y *(3.+A.x)/8. )
        )
        A = cos( O.a/4. +atan(U.y,U.x) + vec2(0,33) ),
        A.y = length( U +vec2(0, U.y*.3-.13*A) ) -.6 -.2*A.y;
}
