float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

#define OCTAVES 6
float fbm (in vec2 st) {
    // Initial values
    float value = 0.0;
    float amplitude = .5;
    float frequency = 0.;
    //
    // Loop of octaves
    for (int i = 0; i < OCTAVES; i++) {
        value += amplitude * noise(st);
        st *= 2.;
        amplitude *= .5;
    }
    return value;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x / iResolution.y;

    vec2 coord = fragCoord + vec2(0,-50) + vec2(iTime * 100.0, 0.0);

    vec2 uv = (coord / iResolution.xy);
    uv.x *= aspect;

    vec2 grid = floor(uv * 100.0);
    float whiteNoise = noise(grid) * 0.2; // white noise

    float height = fbm(uv.xx) * 400.0; // random height at point

    vec4 groundBaseColor = vec4(0.7490, 0.4705, 0.1490, 1.0); // brown
    vec4 darkGroundBaseColor = vec4(0.4490, 0.1705, 0.05490, 1.0); // dark brown

    vec4 baseColor = groundBaseColor; // dirt

    baseColor = mix(baseColor, darkGroundBaseColor, smoothstep(height - 30.0, height - 100.0, coord.y)); // deeper dirt
    baseColor = mix(baseColor, vec4(vec3(whiteNoise),1.0), smoothstep(height - 180.0, height - 200.0, coord.y)); // bedrock
    baseColor = mix(baseColor, vec4(0.8,0.0,0.0,1.0), smoothstep(height - 260.0, height - 300.0, coord.y)); // lava

    fragColor = baseColor;
    fragColor = mix(fragColor, vec4(vec3(whiteNoise),1.0), 0.5); // add noise
    fragColor *= step(coord.y, height); // limit by height
}
