#define rot(a)      mat2( cos(a), -sin(a), sin(a), cos(a) )

vec2 fold(vec2 inp, float n, float a, float m){
    vec2 v = inp;
    for(float i = 0.; i < n; i++){
        float b=(i/n)*(1.); //-comp
        v-=b; //comp
        v = mod(v*(1.+b*.05),m); //-mod
        v = (v-.5)*rot(.2*(fract(iTime*.04)-.0)*.7)+.8; //-drift
        v=mix(v*v,v,.95); //-w
        v=mix(inp*.4,v,.99); //-w
    }
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float t = iTime;
   // vec3 c = texture(iChannel0, reflect(refract(uv,vec2(1.,.4),1.),fract(uv*2.))).xyz;

   float m = iMouse.y/iResolution.y;
   if(iMouse.y <= 0.) m = .7;
   float a = 1.+m;



   vec2 v = fold(uv, 100., a, m); //-n
 // v=mix(v, v /= fold(v, 30., a, m),.1); //-mixamt
   //v /= fold(v-m, 8., a, m);

   vec3 c = texture(iChannel0, mix(v,uv,.6) ).xyz;

    fragColor = vec4(c, 1.0);
}
