#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(t)(cos((vec3(0,2,-2)/3.+t)*6.24)*.5+.5)
#define N(a)fract(sin(a)*7777.77)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    for(float g,d,e,i=0.,a;
        ++i<99.;
        O.xyz+=mix(vec3(1),H(a),.6)/e/(a<.5?2e4:2e8)
        )
    {
        vec3 r=iResolution,
        p=R(g*normalize(vec3((C-.5*r.xy)/r.y,1.)),
            normalize(vec3(1,2,0)),
            iTime*.2
            );
        a=N(dot(floor(p),vec3(1.234,2.345,3.456)));
        p=fract(p)-.5;
        g+=e=max(5e-7,abs(length(p)-.3)+.001*(step(a,.5)*2.-1.));
    }
}
