float Voronoi(in vec2 p){

	vec2 g = floor(p), o; p -= g;

	vec3 d = vec3(1); // 1.4, etc. "d.z" holds the distance comparison value.

	for(int y = -1; y <= 1; y++){
		for(int x = -1; x <= 1; x++){

			o = vec2(x, y);
            o += hash22(g + o) - p;

			d.z = dot(o, o); 

            d.y = max(d.x, min(d.y, d.z));
            d.x = min(d.x, d.z); 

		}
	}

    return max(d.y/1.2 - d.x*1., 0.)/1.2;


}

vec2 map(vec3 p){

    vec2 d = vec2(0.0);
    bool mt3 = false;
    float c = Voronoi(p.xy/3.0);
    if (c<.07) {c = smoothstep(0.7, 1., 1.-c)*.2;mt3 = true; }
    float pl =  length(1.0- p.z - c) - 2.0 ;//- (1.0- p.z - c);

    d.x = pl;
    if(mt3 == false)
        d.y = 2.0;
    else
        d.y = 3.0;


    return d;

}

vec3 RM(vec3 ro, vec3 rd, float _d){
    vec3 d = vec3(_d, 0.0, 0.0);
    for(int i = 0;i < 32;i++){
        d.yz = map(ro + d.x * rd).xy;

        if(abs(d.y) <(0.001))
            break;

        d.x += d.y;

        if(d.x > 100.0)break;

    }
    if(d.x > 100.0)d.x = -1.0;

    return d;

}


float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}



vec3 getLight(vec3 p, vec3 ro, vec3 rd, vec3 lightpos, out vec3 n, vec2 uv){
    vec2 e = vec2(0.01, 0.0);
    vec2 nd = map(p);

    n = nd.x - vec3(map(p - e.xyy).x ,
                        map(p- e.yxy).x ,
                        map(p- e.yyx).x );

   n = normalize(n);
    //float edge = 0.;
    // n = nr(p, edge);  

    vec3 l = normalize(lightpos-p);

    float occ = calcAO(p, n); 
    float dif = clamp(dot(n, l), 0.0, 1.0);
    dif += occ;




    vec3 sh = RM(p+n*0.01, l, 0.0);
    p = ro + rd * sh.x;


    return vec3(dif);


}

