mat3 quat(vec3 p, float theta){

    vec3 z = normalize(p);

    float t1 =  cos(theta);
    float t2 =  1. - t1;
    float t3 =  z.x*z.x;
    float t6 =  t2*z.x;
    float t7 =  t6*z.y;
    float t8 =  sin(theta);
    float t9 =  t8*z.z;
    float t11 = t6*z.z;
    float t12 = t8*z.y;
    float t15 = z.y*z.y;
    float t19 = t2*z.y*z.z;
    float t20 = t8*z.x;
    float t24 = z.z*z.z;
    return mat3( t1 + t2*t3, t7 - t9, t11 + t12, t7 + t9, t1 + t2*t15, t19 - t20, t11 - t12, t19 + t20, t1 + t2*t24);

}

mat3 quaternion(vec3 v, float an){

    vec3 z = normalize(v);

    float a = cos(an/2.0);
    float s = sin(an/2.0);
    float b = s*z.x;
    float c = s*z.y;
    float d = s*z.z;
    float a2 = a*a;
    float b2 = b*b;
    float c2 = c*c;
    float d2 = d*d;
    float _2ab = 2.*a*b;
    float _2ac = 2.*a*c;
    float _2ad = 2.*a*d;
    float _2bc = 2.*b*c;
    float _2bd = 2.*b*d;
    float _2cd = 2.*c*d;

    return mat3(a2+b2-c2-d2  , _2bc-_2ad, _2ac+_2bd, 
                _2ad+_2bc, a2-b2+c2-d2  , _2cd-_2ab,
                _2bd-_2ac, _2ab+_2cd, a2-b2-c2+d2);


}

