const mat2 rotate2D = mat2(1.3623, 1.7531, -1.7131, 1.4623);
vec2 add = vec2(1.0, 0.0);
#define HASHSCALE1 .1031
#define HASHSCALE3 vec3(.1031, .1030, .0973)
#define HASHSCALE4 vec4(1031, .1030, .0973, .1099)

float Hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}
vec2 Hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * HASHSCALE3);
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.xx+p3.yz)*p3.zy);

}

float Noise( in vec2 x )
{
    vec2 p = floor(x);
    vec2 f = fract(x);
    f = f*f*(3.0-2.0*f);

    float res = mix(mix( Hash12(p),          Hash12(p + add.xy),f.x),
                    mix( Hash12(p + add.yx), Hash12(p + add.xx),f.x),f.y);
    return res;
}

float TerrainRM(in vec2 p){

    vec2 q = p * 0.05;
    float w = Noise(q);
    w = 5.*w;
    float f = 0.0;
    for(int i = 0;i < 5;i++){
        f += w * Noise(q*0.85);
        w = -w * 0.4;
        q = rotate2D * q;

    }

    float ff = Noise(q * 0.002);
    f += ff*20.;//-80.0;

    return f;


}


// https://iquilezles.org/articles/smin
float smin( float a, float b, float k )
{
    float h = max(k-abs(a-b),0.0);
    return min(a, b) - h*h*0.25/k;
}

// https://iquilezles.org/articles/smin
float smax( float a, float b, float k )
{
    k *= 1.4;
    float h = max(k-abs(a-b),0.0);
    return max(a, b) + h*h*h/(6.0*k*k);
}



float _line(vec2 p, vec2 a, vec2 b){

    vec2 pa = p-a;
    vec2 ba = b-a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
    return length(pa - ba*h);
}

vec3 Line(vec2 p, vec2 a, vec2 b, float l1, float l2){

    float d = _line(p, a, b);
    float m = smoothstep(l1, l2, d);
    return vec3(m); 

}

float _line3(vec3 p, vec3 a, vec3 b){

    vec3 pa = p-a;
    vec3 ba = b-a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
    return length(pa - ba*h);
}

vec3 Line3(vec3 p, vec3 a, vec3 b, float l1, float l2){

    float d = _line3(p, a, b);
    float m = smoothstep(l1, l2, d);
    return vec3(m); 

}
