float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}


float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float rand(float t)
{
    return hash11(t);
}


float cubicPulse( float c, float w, float x ){
    x = abs(x - c);
    if( x>w ) return 0.0;
    x /= w;
    return 1.0 - x*x*(3.0-2.0*x);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //uv = (uv + 0.5) * vec2(iResolution.x/iResolution.y, 1.0);

    float t = iTime/5.0;
    float step = floor(t);
    t = smoothstep(0.1, 0.9, t-step);

    float Z = 3.0+8.0*rand(step);
    float dir = sign(mod(floor(Z*uv.y), 2.0)-0.5);

    vec2 uvg = vec2(uv.x, uv.y);
    uvg.x = fract(uv.x+dir*t);


    //vec2 uv2 = (floor(Z*uv)+offset)/Z;


    // Output to screen
    fragColor =  texture(iChannel0, uvg);
    //fragColor =  vec4(uvg.xy, 0.0, 1.0);
}
