void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( fragCoord-.5*iResolution.xy )/iResolution.y;

    // Time varying pixel color
     vec3 col = 0.5+ 0.5*sin(iTime+uv.xyx+vec3(0,2,4));

    float ray = iResolution.x*10.;
    vec2 center= vec2(iResolution.x/2.,iResolution.y/2.);
    float  xa =(fragCoord.x - center.x)*(cos(iTime));
    float xb = (fragCoord.y - center.y)*(cos(iTime));

    if( ( xa*xa + xb*xb ) <= ray){
         col = 0.5+ 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}
