void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	uv -=0.5;
    uv.x = uv.x * iResolution.x / iResolution.y;
    vec3 red = vec3(.937, .20, .251);
	vec3 yellow = vec3(0.973, 0.451, 0.224);
    vec3 outColor = mix(yellow, red, abs(sin(iTime)));
    outColor += smoothstep(3./10.,3./10.+0.005,distance(vec2(0.),uv));

    // Output to screen
    fragColor = vec4(outColor,1.0);
}
