vec2 center1 = vec2(100,100);
float radius1 = 30.0;
vec2 center2 = vec2(100,100);
float radius2 = 30.0;

float startX=150.0;

float metaball(vec2 point ,vec2 center,float radius){
    vec2 lenDir = point - center;
    float magSqr = lenDir.x*lenDir.x + lenDir.y*lenDir.y;

    float res = radius*radius / magSqr;
    return res;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    center2.x= startX+ sin(iTime)*100.0;

    if( metaball(fragCoord,center1,radius1) + metaball(fragCoord,center2,radius2) >=1.0){
       fragColor= vec4(0,1,0,0);
    }else{
       fragColor = vec4(0,0,0,0);
    }
}
