//This isn't supposed to be fast. It's to demonstrate how it works.
//Only view at 100%  zoom in your browser!

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float border = 0.6;
    float darksubpixel = 0.25;

    vec2 uv = fragCoord.xy / (iResolution.xy);        

    float mh =  fract(fragCoord.x/3.0);

    float mh2 =  fract(fragCoord.x/6.0);
    float mv2 =  fract(fragCoord.y/6.0);

    vec2 p = uv;
    vec4 col = texture(iChannel0,uv);

    if (mh < 0.3333333)
        col.rb *= vec2(darksubpixel);
    if (mh >= 0.3333333 && mh < 0.6666667)
        col.g *= darksubpixel;
    if (mh >= 0.6666667)
        col.rgb *= darksubpixel;

    if (mv2 > 0.833333 && mh2 > 0.5)
        col.rgb*=border;

    if (mv2 > 0.333333 && mv2 <0.5 && mh2 < 0.5)
        col.rgb*=border;

    fragColor = vec4(col.rgb,1.0);
}
