void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = ((fragCoord.xy / iResolution.xy)-.5)*2.;	// 0 ~ 1 -> -1 ~ 1

    float d = length(pos);
    vec3 col = vec3(1.-d*sin(iTime*2.5), 1.-d*sin(iTime*3.)+.3, 1.-d*sin(iTime*2.) - .2);

    fragColor = vec4(abs(col), 1.0);
}
