/**
 * Convert r, g, b to normalized vec3
 */
vec3 rgb(float r, float g, float b) {
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}

/**
 * Draw a rectangle at vec2 `pos` with width `width`, 
height `height` and color `color`.
 */
vec4 rectangle(vec2 uv, vec2 pos, float width, float height, 
               float skew, vec3 color) 
{
    float x = uv.x; float y = uv.y;
	float t = 0.0;

    x += y*skew;

	if ((x > pos.x - width / 2.0) 
        && (x < pos.x + width / 2.0)
		&& (y > pos.y - height / 2.0) 
        && (y < pos.y + height / 2.0)) 
    {
		t = 1.0;
	}

	return vec4(color, t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
    vec2 uv = fragCoord / iResolution.xy; // 0 <> 1
    uv -= .5; // -0.5 <> 0.5
    uv.x *= iResolution.x / iResolution.y; // aspect ratio

	// Color
	vec3 yellow = rgb(255.0, 255.0, 0.0);
    // Empty rectangle
    vec4 text = rectangle(uv, vec2(0.,0.), 0., 0., 0., yellow);

    // A letter
	vec4 A = rectangle(uv, vec2(-0.21,0.), 0.05, 0.35, -0.3, yellow);
	A += rectangle(uv, vec2(-0.1,0.), 0.05, 0.35, 0.3, yellow);
    A += rectangle(uv, vec2(-0.15,-0.03), 0.13, 0.04, 0., yellow);
    text += A;

    // K letter
    vec4 K = rectangle(uv, vec2(0.05,0.), 0.05, 0.35, 0., yellow);
    K += rectangle(uv, vec2(0.09,0.083), 0.05, 0.183, -0.8, yellow);
    K += rectangle(uv, vec2(0.09,-0.083), 0.05, 0.183, 0.8, yellow);
    text += K;

    //vec4 line = rectangle(uv, vec2(-0.15,-0.183), 0.25, 0.01, 0., yellow);
    //line += rectangle(uv, vec2(0.15,-0.183), 0.25, 0.01, 0., yellow);
    //text += line;

    // Background layer
	vec4 background = vec4(rgb(144.0, 144.0, 144.0), 1.0);
	// Blend the two
    fragColor = mix(background, text, text.a);

}
