const float c_sampleDensity = 0.01f;   // density of the white dots on the black background
const float c_pixelScale = 8.0f;       // higher numbers = zooming in

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    vec2 bnFragCoord = fragCoord / c_pixelScale;
    vec2 bnFragCoordFract = abs(fract(bnFragCoord) - 0.5f);

	float dx = fwidth(length(bnFragCoord));

    vec2 bnuv = (floor(bnFragCoord) + 0.5f) / vec2(textureSize(iChannel0, 0));

    float s = texture(iChannel0, bnuv).r > c_sampleDensity ? 0.0f : 1.0f;
    s *= smoothstep(0.4+dx, 0.4-dx, sqrt(dot(bnFragCoordFract,bnFragCoordFract)));

    fragColor = vec4(s,s,s,1.0);
}
