#define effect1 abs(sin(iTime * 10. * uv.x))
#define effect2 abs(sin(iTime * 10. * uv.y))
#define effect3 abs(sin(iTime * 100. * uv.x * uv.y))

float sdCircle(vec2 uv) {
	return 1. - length(uv);
}

float ring(vec2 uv, float r) {
    float d = sdCircle(uv);
    r = 1. - r * effect1;
    float blur = 0.02;
	return smoothstep(r - blur, r       , d) - 
           smoothstep(r       , r + blur, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;

    float ring1 = ring(uv, 0.5);

    vec3 col = vec3(ring1);

    fragColor = vec4(col,1.0);
}
