int lehmerSeed=0;

void srandomLehmer(vec2 uv,float zoom)
{
 uv*=zoom;
 int a=int(uv.x)<<16;
 int b=int(uv.y);
 lehmerSeed=a^b;
}

#define ZOOM (1000.0)

float randomLehmer()
{
     int tmp=0;
     tmp=lehmerSeed*1234567890;
     tmp=(tmp>>16)^tmp;
     tmp=tmp*1234567890;
     return float((tmp>>16)^tmp)/float(2147483647);

    //real lehmer
