     int multiplier1=0;
     lehmerSeed+=0xe120fc15;
     tmp=lehmerSeed*0x4a39b70d;
     multiplier1=(tmp>>16)^tmp;
     tmp=multiplier1*0x12fad5c9;
     return float((tmp>>16)^tmp)/float(2147483647);*/
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.y=-uv.y;

    srandomLehmer(uv,iTime*iTime);
    float col = randomLehmer();

    // Output to screen
    fragColor = vec4(col,0.0,0.0,1.0);
}
