void mainImage( out vec4 O, vec2 U )
{
  //O = T(U+vec2(0,1));
    O.r = T(U+vec2(0,1)).r;  // vertical scroll at different speed in r,g,b
    O.g = T(U+vec2(0,2)).g;
    O.b = T(U+vec2(0,3)).b;

    vec2 M = iMouse.xy;      // draw
    if (length(M)<30.) M = iResolution.xy* vec2( .6+.4*sin(iTime), .7 );
    float d = smoothstep( 1.5, 0., length(M-U)-10. );
    O += d;
  //O.a = max(O.r,max(O.g,O.b)); O += (1.-O.a) * d;  // true blend
}
