float ease( float x ) {                                 // rewritten from https://shadertoy.com/view/3ddBWB
	return x > .5 ? x = 1.-x, 1. - 4.* x*x*x            // stepier than smoothstep or .5-.5*cos
                  :                4.* x*x*x ;
}

#define B(x)  mod( floor(x), 2. )

void mainImage( out vec4 O, vec2 u )
{   
    O-=O; 
    vec2 R = iResolution.xy,
         U = u *= 8. / R.y,
         I = mod( ceil(U), 4. );
    if (U.x > 8.) { O += .5; return; }                  // out of board

    float t = ease( fract(iTime/2.) ),
          n = B(iTime/2.);

    if (n < 1.) {                                       // phase 1: broup blocks
#define T(x,y)  if ( I.x < 2. ) U.x += I.y==2. ? -t : I.y==3. ? t : 0.
        T(x,y); T(y,x);
    }
    else U *= exp2(t)/2.;                               // phase 2: unzoom

    U = B(U);
    O += float( U.x != U.y );                           // checker cell color          
}
