const vec3 color1 = vec3(1.0);
const vec3 color2 = vec3(0.0, 0.0, 1.0);
const vec3 color3 = vec3(0.0);

const float scale = 4.0e-1;
const float speed = 3.0;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{   
  vec2 p = scale * fragCoord / iResolution.xy;
  p.y *= iResolution.y / iResolution.x;

  float t = speed * iTime;

  vec2 d = p + 5.0e-1 * sin(2.0e-1 * t);

  float dv = sin(1.0e1 * p.x + t)
           + sin(1.0e1 * (p.x * sin(5.0e-1 * t) + p.y * cos(5.0e-1 * t)) + t)
           + sin(sqrt(1.0e2 * dot(d, d)) + t);

  float f = 5.0e-1 * (1.0 + sin(3.0 * dv));

  fragColor = vec4(mix(mix(color1, color2, 2.0 * f), mix(color2, color3, 2.0 * (f - 0.5)), step(0.5, f)), 1.0);
}
