mat2 rot(float a){float s=sin(a),c=cos(a);return mat2(c,-s,s,c);}
float sdTorus( vec3 p, vec2 t ){
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float dist(vec3 p){
    //p.xz*=rot(p.y*4.+iTime);
    //p.yz*=rot(iTime);
    //p.yz*=rot(3.14/2.);
	return length(p)-1.;
    //return sdTorus(p, vec2(.5,.2+.1))*.8;
}

vec3 norm(vec3 p){
    vec2 e = vec2(.01,0.);
	return normalize(vec3(
    	dist(p+e.xyy)-dist(p-e.xyy),
    	dist(p+e.yxy)-dist(p-e.yxy),
        dist(p+e.yyx)-dist(p-e.yyx)
    ));
}

void mainImage( out vec4 fragColor, in vec2 FC )
{
    vec2 uv=(FC*2.-iResolution.xy)/iResolution.y;
    float e,d=0.;
    vec3 p,rd=vec3(uv,1);
    int i;
    for(i=0;i<19;i++){
        p=rd*d-vec3(0.,0.,7.1-cos(iTime)*5.1);
        d+=e=dist(p);
        if(e<.01)break;
    }
    p.xz*=rot(iTime);
    vec3 col=norm(p)*.5+.5;
    fragColor = vec4(vec3(col),1.0);
}
