void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	float noise = abs(sin(sin(iTime*100.1+uv.x*sin(uv.y)*20000000.)));
    //vec4 noise = texture(iChannel1,uv);
    // Time varying pixel color
    vec4 cl = texture(iChannel0,uv);
    uv = vec2(uv.x+(cos(cl.x*2.)+cos(uv.x*3.))/1000.,uv.y+sin(cl.x*2.)/1000.);
    vec2 c = vec2(sin(iTime/3.),cos(iTime/3.))/3.+0.5;
    vec4 col = (texture(iChannel0,(uv-c)/1.02+c)*30.+
        texture(iChannel0,(uv-c)/1.02+c+vec2(1.,0.))+
                texture(iChannel0,(uv-c)/1.02+c+vec2(-1.,0.))+
                texture(iChannel0,(uv-c)/1.02+c+vec2(0.,1.))+
                texture(iChannel0,(uv-c)/1.02+c+vec2(0.,-1.))
         )/34.
        /1.1


        +noise/500.;
	col = sin(col*4.);
    // Output to screen
    fragColor = vec4(col.xxx,1.);
}
