#define ITERATIONS 100

vec2 multiply_complex(vec2 a, vec2 b){
    return vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);
}

float len(vec2 a){
    return sqrt(a.x*a.x + a.y*a.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 c;
    vec2 z = vec2(0);
    vec2 dz = vec2(0);
    float min_side;
    float s_red;
    float s_green;
    float s_blue;
    float dist;
    int i;

    min_side = min(iResolution.x, iResolution.y);
    c = fragCoord.xy/min_side*2.0;
    c = c - vec2(iResolution.x/min_side, iResolution.y/min_side) - vec2(0.5, 0.0);
    for(i = 0; i < ITERATIONS; i++){
        if(z.x*z.x + z.y*z.y > 1000000.0)
            break;
        dz = multiply_complex(dz, z)*2.0 + vec2(1.0);
        z = multiply_complex(z, z) + c;
    }
    if(i == ITERATIONS){
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    } else {
        dist = len(z)*log(len(z))/len(dz) + iTime/20.0;
        if(mod(dist, 0.02) > 0.01)
            s_red = smoothstep(0.0, 0.01, mod(dist, 0.02) - 0.01);
        else
            s_red = smoothstep(0.01, 0.0, mod(dist, 0.02));
        if(mod(dist - 0.053, 0.02) > 0.01)
            s_green = smoothstep(0.0, 0.01, mod(dist - 0.053, 0.02) - 0.01);
        else
            s_green = smoothstep(0.01, 0.0, mod(dist - 0.053, 0.02));
        if(mod(dist - 0.007, 0.02) > 0.01)
            s_blue = smoothstep(0.0, 0.01, mod(dist - 0.007, 0.02) - 0.01);
        else
            s_blue = smoothstep(0.01, 0.0, mod(dist - 0.007, 0.02));
        fragColor = vec4(s_red, s_green, s_blue, 1.0);
    }
}
