#define SURF_DIST .001
#define MAX_DIST 100.
#define MAX_STEPS 100

mat2x2 rot(float a) { return mat2x2(cos(a),sin(a),-sin(a),cos(a)); }

float hash(vec2 p)
{
    return fract(sin(dot(p, vec2(27.754, 78.4476))) * 47125.3567);
}

float sdf(vec3 p)
{
    p -= 0.5;
    p.xy += sin(p.xz*0.8 + iTime)*0.5+0.5;
    p.zy += sin(p.zx*0.3 + iTime)*0.5+0.5;
    vec3 fp = floor(p);
    p = vec3(
        fract(p.x) - 0.5,
        p.y,
        fract(p.z) - 0.5);
    p.xz *= 0.5;
    float l = length(p.xyz) - 0.25 * (sin(iTime + 50.*hash(fp.zx))*0.5+0.5);
    return max(-l, p.y);
}

float march(vec3 ro, vec3 rd) 
{
    float dS, dO;
    vec3 p;
    for(int i=0; i < MAX_STEPS; i++)
    {
    	p = ro + dO * rd;
        dS = sdf(p);
        dO += dS;
        if(dS < SURF_DIST || dO > MAX_DIST) break;
    }
    return dO;
}

vec3 normal(vec3 p)
{
	float d = sdf(p);
	vec2 e = vec2(0.005, 0.0);
	vec3 n = d - vec3(sdf(p - e.xyy), sdf(p - e.yxy), sdf(p - e.yyx));
	return normalize(n);
}

float light(vec3 p, vec3 ro)
{
    vec3 lp = ro + vec3(0.0, 0.0, 2.5);
    vec3 l = normalize(lp - p);
    vec3 n = normal(p);
    return dot(n, l);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
	vec3 rd = normalize(vec3(uv, 1.0));
    rd.zy *= rot(0.4);
    rd.zx *= rot(0.5);
    vec3 ro = vec3(0.0, 5.5, iTime);
    vec3 col;
	float m = march(ro, rd);
    float im = 1.0 / m;
    vec3 p = ro + rd * m;
    col += light(p, ro);
    col = mix(vec3(0.01, 0.05, 0.85), col * col * 2.0, im) * im * im * 100.;
    fragColor = vec4(col,1.0);
}
