float width  = 4.0;
float height = 4.0;

vec4 composite;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2  uv = fragCoord/iResolution.xy;
	float aspect = iResolution.y / iResolution.x;


    uv.x /= aspect;

    uv.x *= width;
    uv.y *= height;

    float w = floor(mod(uv.x, 2.0));
    float h = floor(mod(uv.y, 2.0));

    if (floor(uv.x) < width && (floor(uv.y) < height))
    {
        if ((w > 0.5 && h < 0.5) || (w < 0.5 && h > 0.5))
            composite = texture(iChannel0, uv);
        else
            composite = texture(iChannel1, uv);
    }
    // Output to screen
    fragColor = composite;
}
