float Xor(float a, float b) {
    return a*(1.-b) + b*(1.-a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);

    float a = sin(iTime);//*0.26;
    float s = sin(a+iTime*0.03*sin(-iTime)*0.001);
    float c = cos(a+iTime*0.1);
    uv *= mat2(c,-s,s,c);

    uv *= 32.;
    vec2 gv = fract (uv)-.5;
    vec2 id = floor (uv);

    float d = length(gv);
    float m = 0.;
    float t = iTime;

    for(float y=-1.;y<=1.;y++) {
	    for(float x=-1.;x<=1.;x++) {
        	vec2 offs = vec2(x,y);

    		float d = length(gv-offs);
    		float dist = length(id+offs)*.1;

            float r = mix(.02, 1., sin(dist-(t*2.))*.5+.5);
            m += smoothstep(48./iResolution.y,0., d-r); 
            //m = Xor(m, smoothstep(r,r*.95, d));
        }    
    }

    col += m;    
	fragColor = vec4(col,1.0);
}
