void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    // Modify x coords according to your needs
    float f25_25 = texture(iChannel0, vec2(0.25, 0.25)).x;
    float f75_25 = texture(iChannel0, vec2(0.75, 0.25)).x;
    float f50_25 = texture(iChannel0, vec2(0.50, 0.25)).x;
    float f00_25 = texture(iChannel0, vec2(1.00, 0.25)).x;

    uv.x += 1.0 * 0.025 - f25_25 * 0.05;
	uv.y += 1.0 * 0.025 - f75_25 * 0.05;
    uv.x += 1.0 * 0.025 - f50_25 * 0.05;
	uv.y += 1.0 * 0.025 - f00_25 * 0.05;

    float radius = 0.1 + f50_25 * f00_25 * 0.05;    
    float dist = abs(distance(vec2(0.5), uv) - radius);

    // spin
    float radf = distance(normalize(uv - vec2(0.5)) * radius, vec2(cos(iTime * 2.0), sin(iTime * 2.0)));
    radf *= radf;
    radf *= radf;
    radf *= radf;
    dist -= radf * 0.01;
    // \spin

    float fact = pow(2.0, -dist * 60.);

    fragColor.r = fact * f25_25;
    fragColor.g = fact * f75_25;
    fragColor.b = fact * (f50_25 + f00_25);

    fragColor += vec4(texture(iChannel0, vec2(uv.x * 0.25, 0.5)).x) * 0.1;
    fragColor.r += f25_25 * 0.1;
    fragColor.g += f75_25 * 0.1;
    fragColor.b += f00_25 * 0.1;
}
