void CreateCircle(vec2 uv, vec2 pos, float radius, float blur, inout vec3 result)
{
	uv += pos;

    float myDistance = length(uv);

    float smoothStepResult = smoothstep(radius, radius - blur, myDistance);

    result += smoothStepResult;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 result = vec3(0);

    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    uv -= 0.5;

    CreateCircle(uv, vec2(0.1,0.1), sin(iTime+ 2.) *0.3, sin(iTime  + 3.), result);
    CreateCircle(uv, vec2(-0.5,-0.2), sin(iTime),sin(iTime - 2.) - 1.,result);
    CreateCircle(uv, vec2(-0.2,0),0.5,sin(iTime),result);
    CreateCircle(uv, vec2(0.25,sin(iTime + 2.)), sin(iTime),0.5,result);
    CreateCircle(uv, vec2(0.1,sin(iTime + 5.)), sin(iTime + 3.),0.5,result);
    CreateCircle(uv, vec2(sin(iTime),0),0.5,0.5,result);
    CreateCircle(uv, vec2(sin(iTime + .75),0.3), 0.5,0.5,result);

    result *= vec3(1,0.3,sin(iTime) * 0.4);

    fragColor = vec4(result,1.0);
}
