//#define USE_SMOOTHSTEP

#ifdef USE_SMOOTHSTEP

float smoothClamp(float x, float a, float b)
{
    return smoothstep(0., 1., (x - a)/(b - a))*(b - a) + a;
}

float softClamp(float x, float a, float b)
{
    return smoothstep(0., 1., (2./3.)*(x - a)/(b - a) + (1./6.))*(b - a) + a;
}

#else

float smoothClamp(float x, float a, float b)
{
    float t = clamp(x, a, b);
    return t != x ? t : b + (a - b)/(1. + exp((b - a)*(2.*x - a - b)/((x - a)*(b - x))));
}

float softClamp(float x, float a, float b)
{
 	float mid = (a + b)*0.5;
    return mid + smoothClamp((x - mid)*0.5, a - mid, b - mid);
}

#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

	float low = 0.1;
    float high = (iMouse.z == 0.) ? 0.7 : iMouse.y/iResolution.y;

    fragColor.r = 1. - 0.5*abs(uv.y - smoothClamp(uv.x, low, high))*iResolution.y;
    fragColor.g = 1. - 0.5*abs(uv.y -   softClamp(uv.x, low, high))*iResolution.y;
    fragColor.b = 1. - 0.5*abs(uv.y -       clamp(uv.x, low, high))*iResolution.y;
    fragColor.a = 1.;   
}
