#define A(v)      clamp( 1.5*r - min(s,2e2) * abs(v) , 0., 1. ) * min(1.,s/2e2)            //
#define C(C,r)    A( length(C-U) - r )                                //
#define L(x,y)    ( a=b, b = vec2(x,y),                                \
                    A( length( U-a - (b-a) * clamp(dot(U-a, b-a) / dot(b-a, b-a), 0., 1.) )) ) //
void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy,
         U = u / R.y, a, b, B = vec2(.39,.31);
    O += exp2(-length(1.-u/R)/1.4)-.5;        /* shift to white */
    float t = exp2(fract(.2*iTime)), r = 1. + .5*t, s = R.y*t;
    R /= R.y;
    U = ( U + (t-1.) * R ) / t;                /* infinite zoom */
    for( int i=0; ++i<8; U += U-R, s/=2. ) {   /* inception     */
        b = B; O += C(.4,.08) +  L(.3,.1) +  L(.6,.1);  /* body */
        b = B; O += L(.7,.1)  +  L(1 ,.3) +  L(1.2 ,.1) + L(1.25,.15);
        b = vec2(.7,.09); O += L(1.05,.15)+  L(1.35,.1) + L(1.4 ,.15);
        r *= .7;                                       /* dream */
        O += C(R*vec2(.31,.54),.03) + C(R*vec2(.348,.62),.03);
        for ( t=0. ; t < 1.6; t+= .1 )
            O += C( R - R*.69* cos(t+vec2(0,11)), .03);
    }
}
