#define field(U) 10.* vec2( f1(U),f2(U))
#define PI 3.14159265359


float scale = 1.2;


float f1(vec2 U)
{    
    U = U.y * ( vec2(5,3));
    return sin(U.x) + cos(U.y);
}

float f2(vec2 U)
{
    U = U.x * ( vec2(5,3));
    return sin(U.y) - cos(U.x);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - 0.85;

	uv.x *= iResolution.x/iResolution.y;

    vec4 col,col1, col2;

    uv *= scale + 0.35*sin(iTime*0.05);


    vec2 fld1 = field(uv);

    vec4 iColor = vec4(0.8,1.0,1.0,1.0);

    col1 = vec4(1.0);

    col1 += sin(iColor + fld1.x + fld1.y)*10.0;   

    uv *= scale + 0.35*sin(iTime*0.05);

    vec2 fld2 = field(uv);

    col2 += sin(col1 + fld2.x - fld2.y)*10.0;

    col = mix(col1,col2,abs(sin(0.5*iTime)));


	fragColor = col;
}
