float D(vec3 p, vec3 r)
{
    float dist = (p.x-r.x)*(p.x-r.x) + (p.y-r.y)*(p.y-r.y) + (p.z-r.z)*(p.z-r.z);
    return 1.*exp(-4.5*dist);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.*fragCoord/iResolution.xy - vec2(1.,1.);

   	float intensity = D(vec3(uv,1.0), vec3(0.0,0.0,0.0));

    for (int i = 0; i < 2; i++) {
        intensity += D(vec3(uv,1.0), vec3(float(i)-.5 + .4*float(i)*sin(iTime) -.1*float(i),0.,1.0));
    }

    // Output to screen
    fragColor = vec4(mix(vec3(0.0,0.0,0.0), vec3(1.0,0.0,0.0), intensity),1.0);
}
