vec3 calcNormal( in vec3 pos )//normals calculation
{
    vec3 eps = vec3(0.0001,0.0,0.0);

    return normalize( vec3(
      map( pos+eps.xyy, 0.0).x - map( pos-eps.xyy, 0.0).x,
      map( pos+eps.yxy, 0.0).x - map( pos-eps.yxy, 0.0).x,
      map( pos+eps.yyx, 0.0).x - map( pos-eps.yyx, 0.0).x ) );
}



float fresnel(in vec3 I, in vec3 N, float eps) 
{ 
    float cosi = clamp( dot(I, N), -1.0, 1.0); 

    float etai = 1.0;
    float etat = eps; 
    float tmp;
    if (cosi > 0.0) { tmp = etai; etai = etat; etat = etai; /*swap(etai, etat);*/ } 

    // Compute sini using Snell's law
    float sint = etai / etat * sqrt(max(0.0, 1.0 - cosi * cosi)); 
    // Total internal reflection
    if (sint >= 1.0) { 
        return 1.0;
    } 
    else { 
        float cost = sqrt(max(0.0, 1.0 - sint * sint)); 
        cosi = abs(cosi); 
        float Rs = ((etat * cosi) - (etai * cost)) / ((etat * cosi) + (etai * cost)); 
        float Rp = ((etai * cosi) - (etat * cost)) / ((etai * cosi) + (etat * cost)); 
        return  (Rs * Rs + Rp * Rp) / 2.0; 
    } 
    // As a consequence of the conservation of energy, transmittance is given by:
    // kt = 1 - kr;
} 


