//#define L(a,b) max(0.,1.- R.y/6. * length( U-a - (b-a) * clamp( dot(U-a, b-a) / dot(b-a, b-a), 0., 1.) ) ) //
#define L(a,b) max(0.,1.5- 2e2 * length( U-a - (b-a) * clamp( dot(U-a, b-a) / dot(b-a, b-a), 0., 1.) ) ) //
void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy,
         U = ( u+u - R ) / R.y;\
         U.x = abs(U.x), U.y = .2 - U.y;
    float l = length(U), a = l+l - iTime;
    O += max(0., .5*sin( 30.*( a + sin(3.*a) ) )) * max(0.,cos( 3.*U.y/l ))
      +  L(vec2(.1,1.12) , ) + L(vec2(0,1.12),);
    U.y < 1.1 ? U.x /= U.y, U.y = .95 + abs( mod(U.y,.1) / U.y -.05 ),
    O += L( vec2(0,.95), vec2(.1,1 ) ) : O;}
