const float trt = 1.0594630943592952645;
const float bpm = 140.0;

float note2freq(float note) {
    return 440.0 * pow(trt,note);
}

float sawWave(float time, float freq, float amp) {
 	   return mod(time,1.0/freq)*freq*amp;
}

vec2 bassSynth(float time, float tempo) {
    float m = floor(mod(tempo/2.0,8.0)); //minim number
    float note = floor(mod(tempo*2.0,2.0)) * 12.0 - 35.0 +
        float(m >= 4.0  && m < 6.0) * (5.0 + (m-4.0) * 2.0) ;
    return vec2(sawWave(time, note2freq(note), exp(-0.0*mod(tempo, 0.5))));
}

vec2 leadSynth(float time, float tempo) 
{
    float q = floor(mod(tempo*2.0,32.0)); //quaver number
    float ampMask = float( //rhythm
        (q > 0.0 && q < 6.0) || 
        (q > 8.0 && q < 17.0) ||
    	q == 20.0 || q == 22.0 || q == 23.0);
    float note = -11.0 + //melody
        float(q<13.0) * floor(0.9 +  2.1 * (mod(q-1.0,4.0))) + 
        float(q>=13.0 ) * (21.0-q) + 
        float(q==15.0 ) * (-3.0) +
        float(q>=20.0 ) * (2.0) +
        float(q==22.0 ) * (6.0);  
    return vec2(sawWave(time, note2freq(note+0.01), ampMask * exp(-1.0*mod(tempo, 0.5))),
                sawWave(time, note2freq(note-0.01), ampMask * exp(-1.0*mod(tempo, 0.5))));
}



vec2 leadSynthDirecto(float time, float tempo, float pnote) 
{
    float q = floor(mod(tempo*2.0,32.0)); //quaver number
    float ampMask = float(1.0); //rhythm

    float note = pnote; //melody

    return vec2(sawWave(time, note+0.01, ampMask * exp(-1.0*mod(tempo, 0.5))),
                sawWave(time, note-0.01, ampMask * exp(-1.0*mod(tempo, 0.5))));
}

vec2 kickSynth(float time, float tempo) {
    const float dist = 3.0;
    tempo = mod(tempo,1.0); 
    return vec2(clamp(sin(pow((tempo+0.2)*0.0001,-0.5))*exp(-3.0*tempo),-(1.0/dist),(1.0/dist)) * dist);
}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec2 clapSynth(float time, float tempo) {
    float q = floor(mod(tempo*2.0,32.0)); //quaver number;
    float b = floor(q/8.0);//bar number
    float qb = mod(q,8.0); //quaver in bar;
    float ampMask = float((qb >= 2.0 && qb < 4.0) || (qb >= 6.0 && qb < 7.0)
                         || (b == 3.0 && (qb == 1.0 || qb >= 5.0))); 
    tempo = mod(tempo*4.0 ,2.0);
    return vec2(rand(vec2(time+1.2,tempo+6.1)) * ampMask * exp(-1.5*tempo),
                rand(vec2(time+2.1,tempo+1.6)) * ampMask * exp(-1.5*tempo));
}


float calf(float i){
  return pow(2.0,i/12.0);
}

vec2 leadvotroV01(float time, float tempo)
{
  float tbpm = time*bpm/60.0;
  float[8] seq_line = float[](0.,7.,5.,10.,7.,3.,5.,-2.);
  float seq_time = mod(floor(tbpm),8.0);
  float seq_freq = 440.0*calf(seq_line[int(seq_time)]);
 vec2 lead = leadSynthDirecto(time*1.005,tempo,seq_freq) * 0.333;
    return lead;
}

vec2 leadvotroV02(float time, float tempo)
{
  float tbpm = time*bpm/60.0;
  float[10] seq_line = float[](0.0,2.0,12.0,2.0,7.0,8.0,7.0,8.0,12.0,6.);
  float seq_time = mod(floor(tbpm),10.0);
  float seq_freq = 240.0*calf(seq_line[int(seq_time)]);

    vec2 lead = leadSynthDirecto(time*1.005,tempo,seq_freq) * 0.333;

    return lead;
}

