//autor desconocido
const float pi= 3.14159265359;
const float spinspeed=0.2;
const float movespeed=-1.;
const float spirals=3.;
const float twist = 3.0;



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy-0.5*iResolution.xy)/iResolution.y;

	float l = length(uv);
	float pos = atan(uv.x, uv.y) + fract(spinspeed/iTime) * pi;
	float distortion = iTime*movespeed + twist / sqrt(l);
	vec2 s = 2.0*abs(fract(vec2(pos + distortion, 2.0*(pos - distortion + spirals))/pi*twist) - vec2(0.5));
	float d = dot(s,s);
	float f = smoothstep(0.0, 0.1, 1.0-d);
	vec3 color = vec3(f*l);

	fragColor = vec4(color, 1.0);
}
